from gnews import GNews# Create a GNews objectgoogle_news = GNews(period='3d')# Define the list of keywordskeywords = [    "OpenAI",    "Anthropic"]# Fetch the top news for each keyword and print the first news itemfor keyword in keywords:    news = google_news.get_news(keyword)    if news:        article = news[0]  # Get the first article        print(f"News for '{keyword}':")        print(f"Title: {article['title']}")        print(f"Link: {article['url']}")        print(f"Description: {article['description']}")        print(f"Published Date: {article['published date']}")        print(f"URL: {article['url']}")        print(f"Publisher: {article['publisher']['title']}")        print(f"Publisher URL: {article['publisher']['href']}")    else:        print(f"No news found for '{keyword}'")