from gnews import GNews# Create a GNews objectgoogle_news = GNews()# Define the list of keywordskeywords = [    "Artificial Intelligence",    "Large model",    "Machine learning",    "Deep learning",    "Transformer",    "Hugging Face",    "Andrew Ng",    "OpenAI",    "Anthropic"]# Fetch the top news for each keywordnews_by_keyword = {}for keyword in keywords:    news_by_keyword[keyword] = google_news.get_news(keyword)# Print the news for each keywordfor keyword, news in news_by_keyword.items():    print(f"News for '{keyword}':")    for article in news:        print(f"Title: {article['title']}")        print(f"Link: {article['url']}")        print(f"Title: {article['title']}")        print(f"Description: {article['description']}")        print(f"Published Date: {article['published date']}")        print(f"URL: {article['url']}")        print(f"Publisher: {article['publisher']['title']}")        print(f"Publisher URL: {article['publisher']['href']}")